<?phpfunction cb_error_handler($errno, $errstr, $errfile, $errline, $errctx) {   if ($errno!=8)	echo "($errfile > $errline): $errno = $errstr"."<br/>".$errctx."<br/>"."<br/>";	//Gestor::greg_log("ERROR!!!!!!!!!!!!!!!!!!!!!!!!!! -- $errfile > $errline): $errno = $errstr"."<br/>".$errctx."<br/>"."<br/>");}set_error_handler("cb_error_handler");//ini_set ('error_reporting', E_ERROR & ~E_NOTICE);//error_reporting(E_ERROR & ~E_NOTICE);  ini_set ('error_reporting', E_ALL);error_reporting(E_ERROR);  ?>